// Log what data we're receiving for debugging
const inputData = $input.all();
console.log('Input data:', JSON.stringify(inputData, null, 2));

// Extract audio URL or file information
let audioUrl = '';
let fileInfo = {};

// Check if data is in first array item
if (Array.isArray(inputData) && inputData.length > 0) {
  console.log('Processing array item');
  const item = inputData[0];

  // Check if we have binary data (for file info only)
  if (item.binary) {
    const binaryPropertyName = Object.keys(item.binary)[0];
    const binaryData = item.binary[binaryPropertyName];

    fileInfo = {
      fileName: binaryData.fileName,
      fileSize: binaryData.fileSize,
      mimeType: binaryData.mimeType
    };
  }

  // Check if we have a direct URL in the JSON
  if (item.json && item.json.url) {
    audioUrl = item.json.url;
  }
  // Or if we have an audio_url property
  else if (item.json && item.json.audio_url) {
    audioUrl = item.json.audio_url;
  }
}

// If no URL found but we have webhook data with URL
if (!audioUrl && $input.first().json) {
  const json = $input.first().json;
  if (json.url) {
    audioUrl = json.url;
  } else if (json.audio_url) {
    audioUrl = json.audio_url;
  }
}

// If we have binary data but no URL, we could generate a temporary URL or use a shared location
// For now, we'll return detailed information for debugging
if (!audioUrl) {
  console.log('No audio URL found in the input data');

  // If we have binary data, we can mention that we have the file but need a URL
  if (Object.keys(fileInfo).length > 0) {
    return {
      json: {
        error: 'No audio URL provided, but file data is available',
        fileInfo: fileInfo,
        message: 'Please provide a URL to the audio file or upload it to a publicly accessible location',
        inputStructure: JSON.stringify(inputData, null, 2)
      }
    };
  } else {
    // No URL and no file info
    return {
      json: {
        error: 'No audio URL or file data found',
        message: 'Please provide a URL to the audio file',
        example: { url: 'https://example.com/audio.mp3' },
        inputStructure: JSON.stringify(inputData, null, 2)
      }
    };
  }
}

// Return the URL to be used with Deepgram API
return {
  json: {
    url: audioUrl,
    fileInfo: fileInfo
  }
};

// Get the transcript text
const plainText = $input.first().json.plainText || '';

// Function to create a simple DOCX file content
function createDocxContent(text) {
  // This is a simplified DOCX file structure
  const docxContent = `
    <?xml version="1.0" encoding="UTF-8" standalone="yes"?>
    <w:document xmlns:w="http://schemas.openxmlformats.org/wordprocessingml/2006/main">
      <w:body>
        <w:p>
          <w:r>
            <w:t>${text}</w:t>
          </w:r>
        </w:p>
      </w:body>
    </w:document>
  `;

  return docxContent;
}

// Function to create SRT content
function createSrtContent(words) {
  if (!words || !Array.isArray(words)) {
    return '';
  }

  let srtContent = '';
  let counter = 1;
  let currentLine = '';
  let startTime = 0;
  let endTime = 0;

  // Group words into lines (max 10 words per line)
  for (let i = 0; i < words.length; i++) {
    const word = words[i];

    if (i % 10 === 0) {
      // Start a new line
      if (i > 0) {
        // Add the completed line to SRT content
        srtContent += `${counter}\n`;
        srtContent += `${formatSrtTime(startTime)} --> ${formatSrtTime(endTime)}\n`;
        srtContent += `${currentLine.trim()}\n\n`;
        counter++;
      }

      // Reset for new line
      currentLine = '';
      startTime = word.start;
    }

    // Add word to current line
    currentLine += word.punctuated_word + ' ';
    endTime = word.end;
  }

  // Add the last line if there's any content
  if (currentLine.trim()) {
    srtContent += `${counter}\n`;
    srtContent += `${formatSrtTime(startTime)} --> ${formatSrtTime(endTime)}\n`;
    srtContent += `${currentLine.trim()}\n\n`;
  }

  return srtContent;
}

// Helper function to format time for SRT
function formatSrtTime(seconds) {
  const hours = Math.floor(seconds / 3600);
  const minutes = Math.floor((seconds % 3600) / 60);
  const secs = Math.floor(seconds % 60);
  const ms = Math.floor((seconds % 1) * 1000);

  return `${padZero(hours)}:${padZero(minutes)}:${padZero(secs)},${padZero(ms, 3)}`;
}

// Helper function to pad zeros
function padZero(num, length = 2) {
  return num.toString().padStart(length, '0');
}

// Get the data from the input
const inputData = $input.first().json;
const status = inputData.status;
const words = inputData.data?.words || [];

// Create file contents
const txtContent = plainText;
const docxContent = createDocxContent(plainText);
const srtContent = createSrtContent(words);

// Return the data with file contents
return {
  json: {
    ...inputData,
    fileContents: {
      txt: txtContent,
      docx: docxContent,
      srt: srtContent
    }
  }
};
